mapper.select:
SELECT a.update_time , a.msg_id , b.ack_time , a.issue_id, a.receiver_topic , a.media_type , a.send_terminal_type , a.msg_size , a.group_id , a.msg_type , b.token_id
FROM pms.message${keyMon} a, pms.ack${keyMon} b
WHERE a.msg_id = b.msg_id AND a.msg_type = 106 AND a.status = 1 AND b.ack_type = 'pma' AND a.update_time >= #{startDate} AND a.update_time < #{endDate} ORDER BY a.update_time limit #{startRow}, #{lengthRow}

SELECT COUNT(*)
FROM pms.message${keyMon} a, pms.ack${keyMon} b
WHERE a.msg_id = b.msg_id AND a.msg_type = 106 AND a.status = 1 AND b.ack_type = 'pma' AND a.update_time >= #{startDate} AND a.update_time < #{endDate}

SELECT a.update_time , a.msg_id , b.ack_time , a.issue_id , b.issue_time, a.receiver_topic , a.media_type , a.send_terminal_type , a.msg_size , a.group_id , a.msg_type , b.token_id
FROM pms.message${keyMon} a, pms.ack${keyMon} b
WHERE a.msg_id = b.msg_id AND a.status = 1 AND a.msg_type IN (10,106) AND b.ack_type = 'pma' AND a.update_time >= #{startDate} AND a.update_time < #{endDate} ORDER BY a.update_time limit #{startRow}, #{lengthRow}

SELECT COUNT(*)
FROM pms.message${keyMon} a, pms.ack${keyMon} b
WHERE a.msg_id = b.msg_id AND a.status = 1 AND a.msg_type IN (10,106) AND b.ack_type = 'pma' AND a.update_time >= #{startDate} AND a.update_time < #{endDate}
