mapper.select:
SELECT a.update_time , a.msg_id , d.ack_time , a.issue_id , a.receiver_topic , a.media_type , a.send_terminal_type , a.msg_size , a.group_id , b.receiver_token_id , b.receiver_ufmi
FROM pms.message201504 a LEFT OUTER JOIN pms.group_msg201504 b ON a.msg_id = b.msg_id LEFT OUTER JOIN pms.ack201504 d ON a.msg_id = d.msg_id and b.receiver_token_id = d.token_id AND d.ack_type = 'pma'
WHERE a.msg_type = 106 AND a.status = 1 AND a.update_time >= #{startDate} AND a.update_time < #{endDate} ORDER BY a.update_time limit #{startRow}, #{lengthRow}

SELECT a.update_time , b.ack_time as pma_ack_time , a.issue_id , a.receiver_topic , a.media_type , a.send_terminal_type , a.msg_size , a.group_id
FROM pms.message${keyMon} a LEFT OUTER JOIN pms.ack${keyMon} b ON a.msg_id = b.msg_id AND b.ack_type = 'pma'
WHERE a.status = 1 AND a.msg_type = 106 AND a.update_time >= #{startDate} AND a.update_time < #{endDate} ORDER BY update_time limit #{startRow}, #{lengthRow}

		SELECT COUNT(*)

		FROM
		pms.message${keyMon}

		WHERE 
		status = 1
		AND msg_type = 106
		AND update_time >=  #{startDate}
		AND update_time < #{endDate}
		ORDER BY update_time
        limit #{startRow}, 10

SELECT COUNT(*)
FROM pms.message201504 a LEFT OUTER JOIN pms.group_msg201504 b ON a.msg_id = b.msg_id LEFT OUTER JOIN pms.ack201504 d ON a.msg_id = d.msg_id and b.receiver_token_id = d.token_id AND d.ack_type = 'pma'
WHERE a.msg_type = 106 AND a.status = 1 AND a.update_time >= #{startDate} AND a.update_time < #{endDate}
