mapper.select:
SELECT issue_id AS user_id , status , is_reservation , media_type , COUNT(msg_id) as msg_cnt , COUNT(pma_ack_type) AS pma_ack_cnt , COUNT(app_ack_type) AS app_ack_cnt
FROM ( select a.issue_id, a.status, a.is_reservation, a.msg_id, a.media_type, b.ack_type AS pma_ack_type, d.ack_type AS app_ack_type, b.token_id FROM pms.message${keyMon} a LEFT OUTER JOIN pms.ack${keyMon} b ON a.msg_id = b.msg_id AND b.ack_type = 'pma', pms.message${keyMon} c LEFT OUTER JOIN pms.ack${keyMon} d ON c.msg_id = d.msg_id AND d.ack_type = 'app' WHERE a.msg_id = c.msg_id
AND a.msg_type = #{msgType}
AND a.update_time > #{dateStart}
AND a.update_time < #{dateEnd}
AND a.issue_id = #{issueId}
UNION select issue_id, status, is_reservation, media_type, msg_id, NULL, NULL , NULL
FROM reservation_msg WHERE status = 0
AND update_time > #{dateStart}
AND update_time < #{dateEnd}
AND issue_id = #{issueId}
) s
GROUP BY user_id, status, is_reservation, media_type ORDER BY user_id, status, is_reservation, media_type

SELECT a.issue_id AS user_id , a.status , a.is_reservation , COUNT(a.msg_id) as msg_cnt , COUNT(b.ack_type) AS pma_ack_cnt , COUNT(d.ack_type) AS app_ack_cnt FROM pms.message${keyMon} a LEFT OUTER JOIN pms.ack${keyMon} b ON a.msg_id = b.msg_id AND b.ack_type = 'pma', pms.message${keyMon} c LEFT OUTER JOIN pms.ack${keyMon} d ON c.msg_id = d.msg_id AND d.ack_type = 'app' WHERE a.msg_id = c.msg_id
AND a.msg_type = #{msgType}
AND a.update_time > #{dateStart}
AND a.update_time < #{dateEnd}
AND a.issue_id = #{issueId}
GROUP BY user_id, status, is_reservation ORDER BY user_id, status, is_reservation

SELECT COUNT(msg_id) / 10 AS TPS
FROM pms.message${value}
WHERE update_time > DATE_SUB(NOW(),INTERVAL 10 SECOND)
