mapper.select:
SELECT user_id
FROM pms.user a
WHERE a.user_id = #{userId}

mapper.update:
UPDATE pms.user
SET
status = #{status}
, issue_time = now()
, issue_id = #{issueId}
WHERE
user_id = #{userId}

UPDATE pms.user
SET role =
#{role}
,user_name = #{userName}
,ip_filters = #{ipFilters}
,status =
#{status}
,message_count_limit = #{msgCntLimit}
,default_expiry =
#{defaultExpiry}
,default_qos = #{defaultQos}
,message_size_limit =
#{msgSizeLimit}
,callback_url = #{callbackUrl}
,callback_method =
#{callbackMethod}
,callback_count_limit = #{callbackCntLimit}
,issue_time = now()
,issue_id = #{issueId}
,ufmi = #{ufmi}
,sa_id =
#{saId}
,group_topics = #{groupTopics}

WHERE user_id = #{userId}

UPDATE pms.user
SET
message_count_limit = #{msgCntLimit}
,issue_time = now()
,issue_id =
#{issueId}
WHERE user_id = #{userId}

UPDATE pms.user
SET message_count_limit = message_count_limit -
#{msgCntLimit}
,issue_time = now()
,issue_id = #{userId}
WHERE user_id =
#{userId}

UPDATE pms.user
SET
password = #{password}
,issue_time = now()
,issue_id = #{issueId}
WHERE
user_id = #{userId}

UPDATE pms.user
SET
user_name = #{userName}
,issue_time = now()
,issue_id = #{issueId}
WHERE
user_id = #{userId}

UPDATE pms.user
SET
user_name = #{userName}
,callback_url = #{callbackUrl}
,callback_method
= 'POST'
,issue_time = now()
,issue_id = #{issueId}
WHERE user_id =
#{userId}
